import React from 'react';
import styled, { keyframes } from 'styled-components';
import { FaReact, FaNodeJs, FaJsSquare, FaCss3Alt, FaHtml5, FaLinkedinIn, FaGithub } from "react-icons/fa";
import Plant from './Plant';


const Header = () => {
  return (
    <Wrapper>
      <Plant />
      <Div>
      <H1>
        Cheers, I'm Heather!
      </H1>
      </Div>
      <Text>
      <div>
        Full-Stack Web Developer with a background in Digital Marketing.
      </div>
      <div>
        Craft Beer Enthusiast. Dog lover.
      </div>
      </Text>
      <Icons><FaReact className="icon" size={50}/> <FaNodeJs className="icon" size={50}/> <FaJsSquare className="icon" size={50}/> <FaCss3Alt  className="icon" size={50}/> <FaHtml5 className="icon" size={50}/></Icons>
    </Wrapper>
  )
}

const Wrapper = styled.div`
  height: 100vh;
  width: 100vw;
  text-align: center;
  display: flex;
  align-items: center;
  flex-direction: column;
`;

const slide = keyframes`
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
`;

const Div = styled.div`
  z-index: 1;
  width: 100vw;
  display: flex;
  justify-content: center;
  /* background-color: #CCF4D5; */
  /* position: sticky;
  top: 0px; */
  text-align: center;
`;

const H1 = styled.h1`
  text-shadow: 8px 8px 0px #9FD9AD;
  margin-top: 50px;
  background-image: url(https://media.giphy.com/media/VIzmAi5KS3RyRhY9Sq/source.gif);
	background-size: cover;
  background-position: center;
	color: transparent;
  -moz-background-clip: text;
	-webkit-background-clip: text;
  padding: 10px 20px;
  width: fit-content;
  font-size: 5rem;
  font-family: 'Righteous', cursive;
  text-transform: uppercase;
  transition: 1s ease;
  animation: ${slide} 2s ease;
`;


const fadeIn = keyframes`
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
`;

const Text = styled.div`
  font-size: 1.2rem;
  line-height: 1.2;
  text-shadow: 2px 2px 0px #9FD9AD;
  animation: ${fadeIn} 5s ease;
  background-color: rgba(204, 244, 213, 0.7);
  padding: 10px;
  border-radius: 10px;
  max-width: 90vw;
`;

const Icons = styled.div`
  position: absolute;
  bottom: 0px;
  padding: 35px 0 40px 0;
  width: 100vw;
  background-color: #9FD9AD;

  .icon {
    margin: 10px;
    color: #414361;
  }
`;

export default Header
