import React from 'react';
import styled, { keyframes } from 'styled-components';
import { FaReact, FaNodeJs, FaJsSquare, FaCss3Alt, FaHtml5, FaLinkedinIn, FaGithub } from "react-icons/fa";


const Header = () => {
  return (
    <Wrapper>
      <H1>
        Cheers, I'm Heather
      </H1>
      {/* <Links>
        <FaLinkedinIn />
        <FaGithub />
      </Links> */}
      <Text>

      <div>
        Full Stack Web Developer with a background in Digital Marketing.
      </div>
      <div>
        Craft Beer Enthusiast.
      </div>
      </Text>
      <Icons><FaReact size={50}/> <FaNodeJs size={50}/> <FaJsSquare size={50}/> <FaCss3Alt size={50}/> <FaHtml5 size={50}/></Icons>
    </Wrapper>
  )
}

const Wrapper = styled.div`
  height: 100vh;
  width: 100vw;
  text-align: center;
  display: flex;
  align-items: center;
  flex-direction: column;
`;

const slide = keyframes`
    from {
       margin-top: -500px;
    }

    to {
       margin-top: 100px;
    }
`;

const H1 = styled.h1`
  margin-top: 100px;
  background-image: url(https://media.giphy.com/media/VIzmAi5KS3RyRhY9Sq/source.gif);
	background-size: cover;
  background-position: center;
	color: transparent;
  -moz-background-clip: text;
	-webkit-background-clip: text;
  padding: 10px 20px;
  width: fit-content;
  font-size: 6rem;
  font-family: 'Righteous', cursive;
  text-transform: uppercase;
  transition: 1s ease;
  animation: ${slide} 3s ease;
  
  &:hover {
    transform: scale(1.05);
  }
`;




const Text = styled.div`
  font-size: 1.2rem;
  line-height: 1.2;
`;

const Icons = styled.div`
  position: absolute;
  bottom: 50px;
`;

export default Header
