import React from 'react';
import styled from 'styled-components';
import Scroll from 'react-scroll';

const Nav = () => {
  return (
    <Wrapper>
        <StyledScroll
        to="header" 
        spy={true} 
        smooth={true} 
        duration={200} 
        className='element' 
        activeClass='active'
      >
      HOME
      </StyledScroll> 
        <StyledScroll
        to="projects" 
        spy={true} 
        smooth={true} 
        duration={200} 
        className='element' 
        activeClass='active'
      >
      PROJECTS
      </StyledScroll> 
        <StyledScroll 
        to="about" 
        spy={true} 
        smooth={true} 
        duration={200} 
        className='element' 
        activeClass='active'
      >
      ABOUT
      </StyledScroll> 
        <StyledScroll
        to="contact" 
        spy={true} 
        smooth={true} 
        duration={200} 
        className='element' 
        activeClass='active'
      >
      CONTACT
      </StyledScroll> 
    </Wrapper>
  )
}
const Wrapper = styled.nav`
  position: fixed;
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  bottom: 0px;
  height: 10vh;
  width: 100vw;
  z-index: 1000;
  background-color: #9FD9AD;
`;

const StyledScroll = styled(Scroll.Link)`
  cursor: pointer;
  &:hover{
    font-weight: 700;
  }
`;

export default Nav;
